Task2

set.seed(10)
n&lt;-100
x&lt;-runif(n)
resp&lt;-3+10*x+rnorm(n)

pred&lt;- predict(lm(resp~ x),data.frame(x))


getRMSE&lt;- function(a,b,c){
  diff=(a-b)^2
  mse=sqrt(1/c(sum(diff)))
  return(mse)
}


getRMSE(resp,pred,n)

[1] 0.1043659



getMAE&lt;- function(a,b,c){
  diff=abs(a-b)
  mae=1/c(sum(diff))
  return(mae)
}


getMAE(resp,pred,n)

[1] 0.01226125
